import { useState } from 'react';
function App() {
  const[button,setButton]=useState(True);
  function eventHandler(){
    if(button == true){
      setButton(false);
    }
    else 
    setButton(True);
  }
  return (
    <div className="App">
    <h1>Toggle button</h1>
    <button style={{color:"red"}} onClick={eventHandler}>Click me</button>
    </div>
  );
}

export default App;
