import axios from "axios";
import { useState,useEffect } from "react";


const LatestMovies=()=>{
    const[loading,setLoading]=useState(false);
    const [value,setValue]=useState(null);
    const[error,setError]=useState(null);

    useEffect(()=>{async function obtain(){
        try{
        setLoading(true)
        const response=await axios.get("http://3.17.216.66:4000/latest")
        console.log(response.data)
        setValue(response.data)
        setLoading(false)
        }
        catch(error){
            setError(error)
        }
       }
       obtain();
    },[])

    const show=()=>{
        return value.map((value,id)=>{ return(<> <Link to="">
            <div>
            <img src={value.imageUrl}></img>
                <h1 key={id}>{value.name}</h1>
                
        </div>
        </Link></>)
        })
    }
    if(loading){
        return <h1>Loading</h1>
    }
    if(error){
        return <h1>{error}</h1>
    }
    return(<>
    <div>Latest Movies Page</div>
    <div>{value && show()}</div>
    </>)
}
export default LatestMovies;
