export default function Toggle(){
    const[button,setButton]=useState(true);
  function eventHandler(){
    if(button){
      setButton(false);
    }
    else 
    setButton(true);
  }
  console.log(button);
  return (
    <div className="App">
    <h1>Toggle button</h1>
    <button  onClick={eventHandler} style={button ? {backgroundColor:"red",fontSize:"30px"} : {backgroundColor:"green",fontSize:"30px"}}>Click Me</button>
    </div>
  );
}
