import { useState } from "react";
export default function Toggle(){
    const[button,setButton]=useState(true);
    const[text,setText]=useState("On");

  function eventHandler(e){
    const a=e.target;
    console.log(a)
    if(button){
      setButton(false);
      setText(e.target.value);
    }
    else 
    setButton(true);
    setText(e.target.value);
  }
  
  console.log(button);
  return (
    <>
    <h1>Toggle button</h1>
    <button  onClick={(e)=>{eventHandler(e)}} style={button ? {backgroundColor:"green",fontSize:"30px"} : {backgroundColor:"red",fontSize:"30px"}} value={button ? "OFF": "ON"}>{text}</button>
    </>
  );
}
