import { useState } from "react";
export default function Toggle(){
    const[button,setButton]=useState(true);
  function eventHandler(e){
    console.log(e.target.value)
    if(button){
      setButton(false);
    }
    else 
    setButton(true);
  }
  console.log(button);
  return (
    <>
    <h1>Toggle button</h1>
    <button  onClick={(e)=>{eventHandler(e)}} style={button ? {backgroundColor:"red",fontSize:"30px",v} : {backgroundColor:"green",fontSize:"30px"}}>Click Me</button>
    </>
  );
}
